USE `pokemon_status`;

DROP TABLE IF EXISTS `combat`;

CREATE TABLE `combat` (
  `Combat_lng_Codigo` int(11) NOT NULL AUTO_INCREMENT,
  `Combat_lng_Pokemon1` int(11) DEFAULT NULL,
  `Combat_lng_Pokemon2` int(11) DEFAULT NULL,
  `Combat_lng_Winner` int(11) DEFAULT NULL,
  PRIMARY KEY (`Combat_lng_Codigo`),
  KEY `Combat_lng_Pokemon1` (`Combat_lng_Pokemon1`),
  KEY `Combat_lng_Pokemon2` (`Combat_lng_Pokemon2`),
  KEY `Combat_lng_Winner` (`Combat_lng_Winner`),
  CONSTRAINT `combat_ibfk_1` FOREIGN KEY (`Combat_lng_Pokemon1`) REFERENCES `pokemon` (`Pokemon_lng_Codigo`),
  CONSTRAINT `combat_ibfk_2` FOREIGN KEY (`Combat_lng_Pokemon2`) REFERENCES `pokemon` (`Pokemon_lng_Codigo`),
  CONSTRAINT `combat_ibfk_3` FOREIGN KEY (`Combat_lng_Winner`) REFERENCES `pokemon` (`Pokemon_lng_Codigo`)
) ENGINE=InnoDB AUTO_INCREMENT=50001 DEFAULT CHARSET=latin1;



DROP TABLE IF EXISTS `pokemon`;

CREATE TABLE `pokemon` (
  `Pokemon_lng_Codigo` int(11) NOT NULL,
  `Pokemon_vch_Name` varchar(255) NOT NULL,
  `Pokemon_vch_Type1` varchar(255) NOT NULL,
  `Pokemon_vch_Type2` varchar(255) NOT NULL,
  `Pokemon_lng_Hp` int(11) NOT NULL,
  `Pokemon_lng_Attack` int(11) NOT NULL,
  `Pokemon_lng_Defense` int(11) NOT NULL,
  `Pokemon_lng_SpAttack` int(11) NOT NULL,
  `Pokemon_lng_SpDefense` int(11) NOT NULL,
  `Pokemon_lng_Speed` int(11) NOT NULL,
  `Pokemon_lng_Generation` int(11) NOT NULL,
  `Pokemon_bln_Legendary` tinyint(1) NOT NULL,
  PRIMARY KEY (`Pokemon_lng_Codigo`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

